#!/usr/local/bin/node

var clip = require('../');
var app = new clip();
app.cli('/*', function(cmd,tty) {
    var files = cmd.params;
    
    if(cmd.stream) {
        cmd.on('data', function(data) {
            tty.log(data);
        })
    }
})
if(require.main) {
  app.run();
}
module.exports = app;
